document.addEventListener("DOMContentLoaded", () => {

    if (document.getElementById('indexSlider')) {
        const sliderBrands = new Swiper('#indexSlider', {
            spaceBetween: 30,
            loop: true,
            autoplay: {
                delay: 15000,
            },
            navigation: {
                nextEl: '#indexSlider .swiper-button-next',
                prevEl: '#indexSlider .swiper-button-prev',
            },
            pagination: {
                el: '#indexSlider .swiper-pagination',
                clickable: true,
            },
            breakpoints: {
                330: {
                    spaceBetween: 10
                },
                991: {
                    spaceBetween: 10
                },
                1200: {
                    spaceBetween: 30
                },
            },
            on: {
                slideChange: function (e) {
                    e.autoplay.run();
                }
            }
        });
    }



});
